];

class Users extends Component {
  constructor() {
    this.state = {
      showUsers: true,
      moreState: "Test",
    };
  }
  toggleUsersHandler() {
    this.setState((curState) => !curState);
  }
  render() {
    return (
      <div className={classes.users}>
        <button onClick={toggleUsersHandler}>
          {showUsers ? "Hide" : "Show"} Users
        </button>
        {showUsers && usersList}
      </div>
    );
  }
}

